Spell.Code:
    CharData victim = (CharData)target;

    if (victim.IsAffected( Affect.AFFECT_PROTECT_EVIL) && victim.IsAffected( Affect.AFFECT_PROTECT_GOOD))
        return;

    Affect af = new Affect( Affect.AffectType.spell, spell.Name, 24, Affect.Apply.none, 0, Affect.AFFECT_PROTECT_EVIL );

    if (!victim.IsAffected( Affect.AFFECT_PROTECT_EVIL))
        victim.AddAffect(af);

    af = new Affect( Affect.AffectType.spell, spell.Name, 24, Affect.Apply.none, 0, Affect.AFFECT_PROTECT_GOOD );
    if (!victim.IsAffected( Affect.AFFECT_PROTECT_GOOD))
        victim.AddAffect(af);

    if( ch != victim )
        ch.SendText( "Done.\r\n" );
    victim.SendText( "You feel protected from the unbalance and chaos of the world.\r\n" );
    return;
