Spell.Code:
CharData victim = (CharData)target;

if (victim.IsAffected(Affect.AFFECT_VISION_IMPAIRED) || Magic.SpellSavingThrow(level, victim, AttackType.DamageType.black_magic))
{
    ch.SendText( "You have failed.\r\n" );
    return;
}

victim.RemoveAffect(Affect.AFFECT_VISION_ENHANCED);

Affect af = new Affect(Affect.AffectType.spell, spell.Name, 3 + level / 4, Affect.Apply.hitroll, -(level / 11 + 3), Affect.AFFECT_VISION_IMPAIRED);
victim.AddAffect(af);

SocketConnection.Act( "$N&n squints and blinks a few times.", ch, null, victim, SocketConnection.MessageTarget.character );
victim.SendText( "Everything you see is suddenly blurry.\r\n" );
SocketConnection.Act( "$N&n squints and blinks a few times.", ch, null, victim, SocketConnection.MessageTarget.everyone_but_victim );

return;
