Spell.Code:
    CharData victim = (CharData)target;

    if (victim.IsAffected(Affect.AFFECT_STORNOG_SPHERES))
    victim.RemoveAffect( Affect.AFFECT_STORNOG_SPHERES);

    Affect af = new Affect(Affect.AffectType.spell, spell.Name, level / 18, Affect.Apply.none, Math.Max(1, level - 50), Affect.AFFECT_STORNOG_SPHERES);

    victim.SendText( "&+RTranslucent spheres emerge from your hands and begin to gyrate about you.&n\r\n" );
    SocketConnection.Act( "&+RTranslucent spheres emerge from $n's hands and begin to gyrate about $m.&n", victim, null, null, SocketConnection.MessageTarget.room );

    ch.AddAffect(af);
